const tabs=document.querySelectorAll(".galery__item"),content=document.querySelectorAll(".galery__img"),container=document.querySelector(".galery__tabs");function hideTabs(t){content[t].classList.remove("active"),tabs[t].classList.remove("active")}function showTubs(t=0){content[t].classList.add("active"),tabs[t].classList.add("active")}showTubs(),container.addEventListener("click",t=>{let e=t.target;e.classList.contains("galery__item")&&tabs.forEach((t,s)=>{e==t?showTubs(s):hideTabs(s)})});